const cardImage = [{ "src": "/img/helmet-1.png" },{ "src": "/img/potion-1.png" },];


function Home() {

  const shuffleCards = () => {
    const shuffledCards = [...cardImages, ...cardImages]
      .sort(() => Math.random() - 0.5)
      .map(card => ({ ...card, id: Math.random() }))

    setCards(shuffledCards)
    setTurns(0)
  }

  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);


  return (
    <div className="home">
        <div className="diff">
            <h4>Choose the level of difficulty for this game:</h4>
        </div>
        <div className="outer">
            <Link to="/treeontreegame" className="btn btn-info rounded-pill button"  role="button">2x2</Link>
            <Link to="/fouronfourgame" className="btn btn-info rounded-pill button"  role="button">3x3</Link>
            <Link to="/fiveonfivegame" className="btn btn-info rounded-pill button"  role="button">4x4</Link>
        </div>
    </div>
  );
}

export default Home;
